import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormControl, FormGroup, Validators } from '@angular/forms';
import { Categoria } from 'src/app/models/categoria';
import { Cine } from 'src/app/models/cine';
import { Director } from 'src/app/models/director';
import { Pelicula } from 'src/app/models/pelicula';
import { Productora } from 'src/app/models/productora';
import { CategoriasService } from 'src/app/services/categorias.service';
import { CinesService } from 'src/app/services/cines.service';
import { DirectoresService } from 'src/app/services/directores.service';
import { PeliculasService } from 'src/app/services/peliculas.service';
import { ProductorasService } from 'src/app/services/productoras.service';

@Component({
  selector: 'app-nueva-pelicula',
  templateUrl: './nueva-pelicula.component.html',
  styleUrls: ['./nueva-pelicula.component.css']
})
export class NuevaPeliculaComponent implements OnInit {

  imgFile: string = "data:image/jpeg;base64,";
  fileName = '';

  ngOnInit(): void {
    this.cargarCategoria();
    this.cargarDirector();
    this.cargarProductora();
  }

  ListaModels: Pelicula[] = [];

  //CATEGORÍAS
  ListaCategorias: Categoria[] = [];
  nombreCategoria:string = "Categoría no seleccionada";
  idCategoria: number = 0;

  //DIRECTORES
  ListaDirectores: Director[] = [];
  nombreDirector:string = "Director no seleccionado";
  idDirector: number = 0;

  //PRODUCTORAS
  ListaProductoras: Productora[] = [];
  nombreProductora:string = "Productora no seleccionada";
  idProductora: number = 0;


  createSucces: boolean = false;
  createDanger: boolean = false;
  nombre: string = "";
  duracion: number = 0;
  descripcion: string = "";
  id: number = 0;
  Seleccionado: boolean = false;
    
  constructor(
    private http: HttpClient,
    private miService:PeliculasService,
    private categoriaService:CategoriasService,
    private directorService:DirectoresService,
    private productoraService:ProductorasService
    ) { }

  miFormulario = new FormGroup({
    nombre : new FormControl('', [Validators.required]),
    duracion : new FormControl('', [Validators.required]),
    descripcion : new FormControl('', [Validators.required])
  });

  get f(): { [key: string]: AbstractControl} {return this.miFormulario.controls; }

  createModel(){
    if (this.miFormulario.valid){
      const miRequest = {
        'nombre':this.f['nombre'].value, 
        'duracion':this.f['duracion'].value,
        'descripcion':this.f['descripcion'].value,
        'categoria':this.idCategoria,
        'director':this.idDirector,
        'productora':this.idProductora,
        'imagenes':this.imgFile
      }
      console.log(miRequest);
      
      this.miService.store(miRequest).subscribe({
          next: () => [console.log("Creado"), this.createSucces=true, this.createDanger=false],
          error: (e) => [console.error(e),this.createDanger=true, this.createSucces=false],
          complete: () => console.info('complete') 
      })
    }else {
      console.log("Formulario inválido");
      this.createDanger=true;
      this.createSucces=false;
    }
  }

  cargarCategoria(){
    this.categoriaService.index().subscribe({
      next: (r) => [
      console.log(r),
      this.ListaCategorias = r.data
    ],
      error: (e) => [console.error(e)],
      complete: () => console.info('complete') 
    })
  }

  cargarDirector(){
    this.directorService.index().subscribe({
      next: (r) => [
      console.log(r),
      this.ListaDirectores = r.data
    ],
      error: (e) => [console.error(e)],
      complete: () => console.info('complete') 
    })
  }

  cargarProductora(){
    this.productoraService.index().subscribe({
      next: (r) => [
      console.log(r),
      this.ListaProductoras = r.data
    ],
      error: (e) => [console.error(e)],
      complete: () => console.info('complete') 
    })
  }

  selectCategoria(categoria: Categoria){
    this.idCategoria = categoria.id;
    this.nombreCategoria = categoria.nombre;
  }

  selectDirector(director: Director){
    this.idDirector = director.id;
    this.nombreDirector = director.nombre;
  }

  selectProductora(productora: Productora){
    this.idProductora = productora.id;
    this.nombreProductora = productora.nombre;
  }

  onFileChange(event: any){

    if(event.target.files && event.target.files[0]) {
      if(event.target.files[0].type && event.target.files[0].type.split('/')[0] == ["image"] && event.target.files[0].size <= 1024000){
          this.fileName = event.target.files[0];
          var reader = new FileReader();
          reader.onload = (event:any) => {
              this.imgFile = event.target.result;
          }
          reader.readAsDataURL(event.target.files[0]);
      } else {
          console.log("Max image upload size is 1MB only");
      }
    }
  }

}
